import { useEffect, useState } from 'react';
import './App.css';

import abi from './contracts/ABI.json';
import { ethers } from 'ethers';

const contractAddress = "0xF786878156D6d0964CA078D8EB3D7fC89D25d6C2";

function App() {

  const inputRef = useRef(null);

  function handleClick() {
    console.log(inputRef.current.value);
  }

  const [currentAccount, setCurrentAccount] = useState(null);

  const checkWalletIsConnected = async () => {
    const { ethereum } = window;

    if (!ethereum) {
      console.log("Make sure you have Metamask installed!");
      return;
    } else {
      console.log("Wallet exists! We're ready to go!")
    }

    const accounts = await ethereum.request({ method: 'eth_accounts' });

    if (accounts.length !== 0) {
      const account = accounts[0];
      console.log("Found an authorized account: ", account);
      setCurrentAccount(account);
    } else {
      console.log("No authorized account found");
    }
  }

  const connectWalletHandler = async () => {
    const { ethereum } = window;

    if (!ethereum) {
      alert("Please install Metamask!");
    }

    try {
      const accounts = await ethereum.request({ method: 'eth_requestAccounts' });
      console.log("Found an account! Address: ", accounts[0]);
      setCurrentAccount(accounts[0]);
    } catch (err) {
      console.log(err)
    }
  }

  const mintNftHandler = async () => {
    try {
      const { ethereum } = window;

      if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const nftContract = new ethers.Contract(contractAddress, abi, signer);

        console.log("Initialize payment");
        let nftTxn = await nftContract.safeMint("0xeF607f7db89b9E3450B80C5C0D280b2299989231","0x7c0bE6FfDB66b42D51Ca3782A13267769bcAeEb5","https://jsonkeeper.com/b/HHEY");

        console.log("Mining... please wait");
        await nftTxn.wait();

        console.log("Mined, see transaction: https://goerli.etherscan.io/tx/${nftTxn.hash}");

      } else {
        console.log("Ethereum object does not exist");
      }

    } catch (err) {
      console.log(err);
    }
  }

  const connectWalletButton = () => {
    return (
      <button onClick={connectWalletHandler} className='cta-button connect-wallet-button'>
        Connect Wallet
      </button>
    )
  }

  const mintNftButton = () => {
    return (
      <button onClick={mintNftHandler} className='cta-button mint-nft-button'>
        Mint NFT
      </button>
    )
  }

  useEffect(() => {
    checkWalletIsConnected();
  }, [])

  return (
    <div className='main-app'>
      <div class="snowflakes" aria-hidden="flase">  
        <div class="snowflake">
        ❅ 
        </div>
        <div class="snowflake">
        ❅ 
        </div>
        <div class="snowflake">
        ❅ 
        </div>
        <div class="snowflake">
        ❅ 
        </div>
        <div class="snowflake">
        ❅ 
        </div>
        <div class="snowflake">
        ❅ 
        </div>
        <div class="snowflake">
        ❅ 
        </div>
        <div class="snowflake">
        ❅ 
        </div>
        <div class="snowflake">
        ❅ 
        </div>
        <div class="snowflake">
        ❅ 
        </div>
        <div class="snowflake">
        ❅ 
        </div>
